import React, { useState, useEffect, useRef } from "react";
import { DataTable } from "primereact/datatable";
import { Column } from "primereact/column";
import { ProductService } from "./utils/ProductService";
import { Button } from "primereact/button";
import { Toast } from "primereact/toast";
import "./DataTableDemo.css";
import { InputText } from "primereact/inputtext";
import { Dropdown } from "primereact/dropdown";
import { Dialog } from "primereact/dialog";
import Tareas from "../../pages/tareas";


const AdminTable = ({dataUser}) => {
  let emptyUser = {
    id: "",
    user: "",
    password: "",
    permisos: "Comercial",
  };

  const permisos = [
    { name: "Administrador" },
    { name: "Comercial" },
    { name: "Superusuario" },
  ];
  const [products, setProducts] = useState(null);
  const [selectedProducts, setSelectedProducts] = useState(null);
  const [expandedRows, setExpandedRows] = useState(null);
  const [product, setProduct] = useState(emptyUser);
  const [submitted, setSubmitted] = useState(false);
  const [productDialog, setProductDialog] = useState(false);
  const [editUser, setEditUser] = useState(false);
  const toast = useRef(null);
  const isMounted = useRef(false);
  const productService = new ProductService();

  const openNew = () => {
    setProduct(emptyUser);
    setSubmitted(false);
    setProductDialog(true);
  };

  const editProduct = (product) => {
    setProduct(product);
    setSubmitted(false);
    setEditUser(true);
  };

  const actionBodyTemplate = (rowData) => {
    return (
      <div className="actions">
        <Button
          icon="pi pi-pencil"
          className="edit-button p-button-rounded p-button-success mr-2"
          onClick={() => editProduct(rowData)}
        />
      </div>
    );
  };

  const saveProduct = () => {
    setSubmitted(true);
    console.log(product.user);
    console.log(product.permisos);

    if (product.user) {
      let _products = [...products];
      let _product = { ...product };
      if (product.user) {
        toast.current.show({
          severity: "success",
          summary: "Successful",
          detail: "Usuario actualizado",
          life: 3000,
        });
      } else {
        _products.push(_product);
        toast.current.show({
          severity: "success",
          summary: "Correcto",
          detail: "Usuario creado",
          life: 3000,
        });
      }

      setProducts(_products);
      setProductDialog(false);
      setProduct(emptyUser);
    }
  };

  const hideDialog = () => {
    setSubmitted(false);
    setProductDialog(false);
    setEditUser(false);
  };

  const productDialogFooter = (
    <>
      <Button
        label="Cancelar"
        icon="pi pi-times"
        className="p-button-text"
        onClick={hideDialog}
      />
      <Button
        label="Guardar"
        icon="pi pi-check"
        className="p-button-text"
        onClick={saveProduct}
      />
    </>
  );

  const editUserFooter = (
    <>
      <Button
        label="Cancelar"
        icon="pi pi-times"
        className="p-button-text"
        onClick={hideDialog}
      />
      <Button
        label="Guardar"
        icon="pi pi-check"
        className="p-button-text"
        onClick={saveProduct}
      />
    </>
  );

  useEffect(() => {
    if (isMounted.current) {
      const summary =
        expandedRows !== null
          ? "Todos los usuarios expandidos"
          : "Todos los usuarios cerrados";
      toast.current.show({
        severity: "success",
        summary: `${summary}`,
        life: 1000,
      });
    }
  }, [expandedRows]);

  useEffect(() => {
    isMounted.current = true;
    productService
      .getProductsWithOrdersSmall()
      .then((data) => setProducts(data));
  }, []); // eslint-disable-line react-hooks/exhaustive-deps

  const onRowExpand = (event) => {
    toast.current.show({
      severity: "info",
      summary: "Usuario abierto",
      detail: event.data.name,
      life: 3000,
    });
  };

  const onInputChange = (e, name) => {
    const val = (e.target && e.target.value) || "";
    let _product = { ...product };
    _product[`${name}`] = val;

    setProduct(_product);
  };

  const onRowCollapse = (event) => {
    toast.current.show({
      severity: "success",
      summary: "Usuario cerrado",
      detail: event.data.name,
      life: 3000,
    });
  };

  const expandAll = () => {
    let _expandedRows = {};
    products.forEach((p) => (_expandedRows[`${p.id}`] = true));

    setExpandedRows(_expandedRows);
  };

  const collapseAll = () => {
    setExpandedRows(null);
  };

  const rowExpansionTemplate =  (data) => {


    return (
      <div className="orders-subtable">
        <h5>Tareas de {data.user}</h5>
        {console.log(data.user)}
        <Tareas userName={data.user} /> 
      </div>
    );
  };

  const header = (
    <div className="table-header-container">
      <Button
        label="Nuevo usuario"
        icon="pi pi-plus"
        className="p-button-success mr-2"
        onClick={openNew}
      />
      <Button
        icon="pi pi-plus"
        label="Expandir todo"
        onClick={expandAll}
        className="mr-10 button-expand"
      />
      <Button icon="pi pi-minus" label="Cerrar todo" onClick={collapseAll} />
    </div>
  );

  return (
    <div className="datatable-rowexpansion-demo">
      <Toast ref={toast} />
      <Dialog
        visible={productDialog}
        style={{ width: "450px", height: "25rem" }}
        header="Crear usuario"
        modal
        className="p-fluid"
        footer={productDialogFooter}
        onHide={hideDialog}
      >
        <div className="input">
          <label htmlFor="user">Nombre de usuario</label>
          <InputText
            id="user"
            value={product.user}
            onChange={(e) => onInputChange(e, "user")}
            required
            autoFocus
          />
          {submitted && !product.user && (
            <small className="p-invalid">El usuario es necesario.</small>
          )}
        </div>
        <div className="input">
          <label htmlFor="description">Permisos</label>
          <Dropdown
            optionLabel="name"
            id="permisos"
            value={product.permisos}
            options={permisos}
            onChange={(e) => onInputChange(e, "permisos")}
            placeholder="Selecciona permisos"
          />
          {/* 
          <InputText
            id="permisos"
            value={product.permisos}
            onChange={(e) => onInputChange(e, "permisos")}
            required
            autoFocus
          /> */}
          {submitted && !product.rol && (
            <small className="p-invalid">La contraseña es necesaria.</small>
          )}
        </div>
        <div className="input">
          <label htmlFor="description">Contraseña</label>
          <InputText
            id="password"
            value={product.password}
            onChange={(e) => onInputChange(e, "password")}
            required
            autoFocus
          />
          {submitted && !product.password && (
            <small className="p-invalid">La contraseña es necesaria.</small>
          )}
        </div>
      </Dialog>
      <Dialog
        visible={editUser}
        style={{ width: "450px", height: "25rem" }}
        header="Editar usuario"
        modal
        className="p-fluid"
        footer={editUserFooter}
        onHide={hideDialog}
      >
        <div className="input">
          <label htmlFor="user">Nombre de usuario</label>
          <InputText
            id="user"
            value={product.user}
            onChange={(e) => onInputChange(e, "user")}
            required
            autoFocus
          />
          {submitted && !product.user && (
            <small className="p-invalid">El usuario es necesario.</small>
          )}
        </div>
        <div className="input">
          <label htmlFor="description">Contaseña</label>
          <InputText
            id="contraseña"
            value={product.contraseña}
            onChange={(e) => onInputChange(e, "contraseña")}
            required
            autoFocus
          />
          {submitted && !product.contraseña && (
            <small className="p-invalid">La contraseña es necesaria.</small>
          )}
        </div>
      </Dialog>
      <div className="card">
        <DataTable
          value={products}
          expandedRows={expandedRows}
          selection={selectedProducts}
          onSelectionChange={(e) => setSelectedProducts(e.value)}
          onRowToggle={(e) => setExpandedRows(e.data)}
          onRowExpand={onRowExpand}
          onRowCollapse={onRowCollapse}
          responsiveLayout="scroll"
          rowExpansionTemplate={rowExpansionTemplate}
          dataKey="id"
          header={header}
        >
          <Column
            selectionMode="multiple"
            headerStyle={{ width: "2rem" }}
            exportable={false}
          ></Column>
          <Column expander style={{ width: "3em" }} />
          <Column field="user" header="Usuario" />
          <Column field="datacreate" header="Fecha de creación" sortable />
          <Column field="creadopor" header="Creado por" />
          <Column field="rol" header="Rol" sortable />
          <Column
            field="lastsesion"
            header="Último inicio de sesión"
            sortable
          />
          <Column field="tareas" header="Tareas totales" sortable />
          <Column body={actionBodyTemplate}></Column>
        </DataTable>
      </div>
    </div>
  );
};

export default AdminTable;
