import { Route, Routes, Navigate } from "react-router-dom";

import Login from "../pages/login";
import Admin from "../pages/admin";
import Tareas from "../pages/tareas";
import Usuarios from "../pages/usuarios";
import Verify from "../pages/verify";

import { useUserData } from "../utils/auth.context";
import Logout from "../pages/logout";

const isLogedVerify = (state) => {
  if(!state.userName){
    return <Navigate replace to={`/login`}/>
  } else if(!state.verify) {
    return <Navigate replace to={`/verify`}/>
  } 
}


const Router = () => {
  const {state} = useUserData()
  console.log(state)
  return (
    <>
      <Routes>
        <Route exact path='/gestion' element={isLogedVerify(state) ?? <Admin /> }  />
        <Route exact path='/' element={isLogedVerify(state) ?? <Tareas /> }  />
        <Route exact path='/usuarios' element={isLogedVerify(state) ?? <Usuarios />  }  />
        <Route exact path='/logout' element={state.userName ? <Logout /> : <Navigate replace to={`/login`}/>}  />

        <Route exact path='/login' element={state.userName ? <Navigate replace to={`/`}/> :<Login />}  />
        <Route exact path='/verify' element={!state.verify ? <Verify /> : <Navigate replace to={`/`}/>}  />

      </Routes>
    </>
  );
};

export default Router;
