import { Route, Routes, Navigate } from "react-router-dom";

import Login from "../pages/login";
import Tareas from "../pages/tareas"; // This component is the admin panel
import PanelAdmin from "../pages/paneladmin";


import { useUserData } from "../utils/auth.context";
import Logout from "../pages/logout";

const checkLogged = (state) => {
  if(!state.email){
    return <Navigate replace to={`/login`}/>
  }
}


const Router = () => {
  const {state} = useUserData()
  console.log(state)
  return (
    <>
      <Routes>

        <Route exact path='/' element={checkLogged(state) ?? <Tareas /> }  />
        <Route exact path='/paneladmin' element={checkLogged(state) ?? <PanelAdmin /> }  />
        <Route exact path='/logout' element={state.email ? <Logout /> : <Navigate replace to={`/login`}/>}  />
        <Route exact path='/login' element={state.email ? <Navigate replace to={`/`}/> :<Login />}  />

      </Routes>
    </>
  );
};

export default Router;
