import React, { useState, useEffect, useRef } from "react";
import { DataTable } from "primereact/datatable";
import { Column } from "primereact/column";
import { Toast } from "primereact/toast";
import { Toolbar } from "primereact/toolbar";
import { InputTextarea } from "primereact/inputtextarea";
import { RadioButton } from "primereact/radiobutton";
import { Dialog } from "primereact/dialog";
import { InputText } from "primereact/inputtext";
import "./tareas.css";
import ButtonCustom from "../Buttons/button.component";
import ButtonLabel from "../Buttons/buttonLabel.component";
import Dialogs from "../Dialogs/dialogs.component";



const TareasTable = ({ taskData }) => {
  const today = new Date();
  console.log(taskData);
  const date = today.getFullYear()+'-'+(today.getMonth()+1)+'-'+today.getDate();
  let emptyTask = {
    id: null,
    name: "",
    fechacreacion: date,
    description: "",
    asignado: "",
    observaciones: 0,
    ultactualizacion: date,
    cliente: 0,
    tarea: "INSTOCK",
  };

  const [tasks, setTasks] = useState(null);
  const [taskDialog, setTaskDialog] = useState(false);
  const [clientDialog, setClientDialog] = useState(false);
  const [deleteTaskDialog, setDeleteProductDialog] = useState(false);
  const [deleteTasksDialog, setDeleteProductsDialog] = useState(false);
  const [task, setTask] = useState(emptyTask);
  const [selectedProducts, setSelectedProducts] = useState(null);
  const [submitted, setSubmitted] = useState(false);
  const [globalFilter] = useState(null);
  const toast = useRef(null);



  const openNew = () => {
    setTask(emptyTask);
    setSubmitted(false);
    setTaskDialog(true);
  };

  const openDialogClient = () => {
    setTask(emptyTask);
    setClientDialog(true);
  };

  const hideDialogClient = () => {
    setClientDialog(false);
  };

  const hideDialog = () => {
    setSubmitted(false);
    setTaskDialog(false);
  };

  const hideDeleteProductDialog = () => {
    setDeleteProductDialog(false);
  };

  const hideDeleteProductsDialog = () => {
    setDeleteProductsDialog(false);
  };

  const saveTask = () => {
    setSubmitted(true);

    if (task.cliente) {
      let _tasks = [...tasks];
      let _task = { ...task };
      if (task.id) {
        const index = findIndexById(task.id);
        _tasks[index] = _task;
        toast.current.show({
          severity: "success",
          summary: "Successful",
          detail: "Product Updated",
          life: 3000,
        });
      } else {
        _task.id = createId();
        _task.image = "task-placeholder.svg";
        _tasks.push(_task);
        toast.current.show({
          severity: "success",
          summary: "Acción exitosa",
          detail: "Tarea creada",
          life: 3000,
        });
      }

      setTasks(_tasks);
      setTaskDialog(false);
      setTask(emptyTask);
    }

  };

  const editProduct = (task) => {
    setTask({ ...task });
    setTaskDialog(true);
  };

  const confirmDeleteTask = (task) => {
    setTask(task);
    setDeleteProductDialog(true);
  };

  const deleteTask = () => {
    setDeleteProductDialog(false);
    toast.current.show({
      severity: "success",
      summary: "Acción exitosa",
      detail: "Tarea Borrada",
      life: 3000,
    });
    /* const result = DeleteTask(userData, taskData.id).then((data) => console.log(data))
    console.log(result) */

  };

  const findIndexById = (id) => {
    let index = -1;
    for (let i = 0; i < tasks.length; i++) {
      if (tasks[i].id === id) {
        index = i;
        break;
      }
    }

    return index;
  };

  const createId = () => {
    let id = "";
    let chars =
      "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789";
    for (let i = 0; i < 5; i++) {
      id += chars.charAt(Math.floor(Math.random() * chars.length));
    }
    return id;
  };

  const confirmDeleteSelected = () => {
    setDeleteProductsDialog(true);
  };

  const deleteSelectedProducts = () => {
    let _tasks = tasks.filter((val) => !selectedProducts.includes(val));
    setTasks(_tasks);
    setDeleteProductsDialog(false);
    setSelectedProducts(null);
    toast.current.show({
      severity: "success",
      summary: "Acción exitosa",
      detail: "Tareas Borradas",
      life: 3000,
    });
  };

  const onCategoryChange = (e) => {
    let _task = { ...task };
    _task["tarea"] = e.value;
    setTask(_task);
  };

  const onInputChange = (e, name) => {
    const val = (e.target && e.target.value) || "";
    let _task = { ...task };
    _task[`${name}`] = val;

    setTask(_task);
  };

  const leftToolbarTemplate = () => {
    return (
      <React.Fragment>
        <div className="my-2">
          <ButtonLabel labelText="Nueva tareas" icon="plus" action={openNew} />
          <ButtonLabel
            labelText="Borrar tarea"
            icon="trash"
            action={confirmDeleteSelected}
            disabled={!selectedProducts || !selectedProducts.length}
          />
        </div>
      </React.Fragment>
    );
  };

  const buttonTable = (rowData) => {
    return (
      <div className="actions">
        <ButtonCustom action={() => openDialogClient()} icon="info" />
        <ButtonCustom action={() => editProduct(rowData)} icon="pencil" />
        <ButtonCustom action={() => confirmDeleteTask(rowData)} icon="trash" />
      </div>
    );
  };

  const taskDialogFooter = (
    <>
      <ButtonLabel labelText="Cancelar" icon="times" action={hideDialog} />
      <ButtonLabel labelText="Guardar" icon="check" action={saveTask} />
    </>
  );

  const deleteTaskDialogFooter = (
    <>
      <ButtonLabel
        labelText="No"
        icon="times"
        action={hideDeleteProductDialog}
      />
      <ButtonLabel labelText="Sí" icon="check" action={deleteTask} />
    </>
  );

  const deleteTasksDialogFooter = (
    <>
      <ButtonLabel
        labelText="No"
        icon="times"
        action={hideDeleteProductsDialog}
      />
      <ButtonLabel
        labelText="Sí"
        icon="check"
        action={deleteSelectedProducts}
      />
    </>
  );

  return (
    <div className="grid crud-demo">
      <div className="col-12">
        <div className="card">
          <Toast ref={toast} />
          <Toolbar className="mb-4" left={leftToolbarTemplate} />

          <DataTable
            value={taskData === null 
                    ? taskData
                    : taskData === "There are no available tasks" ? null :  taskData
            }
            selection={selectedProducts}
            onSelectionChange={(e) => setSelectedProducts(e.value)}
            dataKey="id"
            paginator
            rows={10}
            rowsPerPageOptions={[5, 10, 25]}
            className="datatable-responsive"
            currentPageReportTemplate="Showing {first} to {last} of {totalRecords} tasks"
            globalFilter={globalFilter}
            emptyMessage="No se han encontrado tareas."
            header="Tareas"
            breakpoint="960px"
            responsiveLayout="stack"
          >
            <Column
              selectionMode="multiple"
              headerStyle={{ width: "2rem" }}
              exportable={false}
            ></Column>
            <Column
              field="taskCreationData"
              header="Fecha de creción"
              sortable
              headerStyle={{ width: "14%", minWidth: "10rem" }}
            ></Column>
            <Column
              field="taskAssignedBy"
              header="Asignado por"
              sortable
              headerStyle={{ width: "14%", minWidth: "10rem" }}
            ></Column>
            <Column
              header="Observaciones"
              field="taskObservations"
              headerStyle={{ width: "14%", minWidth: "10rem" }}
            ></Column>
            <Column
              field="taskLastUpdate"
              header="Última actualización"
              headerStyle={{ width: "14%", minWidth: "10rem" }}
            ></Column>
            <Column
              field="taskType"
              header="Tipo de tarea"
              sortable
              headerStyle={{ width: "14%", minWidth: "10rem" }}
            ></Column>
             <Column
              field="taskDone"
              header="Estado"
              sortable
              headerStyle={{ width: "14%", minWidth: "10rem" }}
            ></Column>
            <Column
              field="taskCliente"
              header="Cliente"
              value="cliente"
              onClick={openDialogClient}
              sortable
              headerStyle={{ width: "14%", minWidth: "10rem" }}
            ></Column>
            <Column body={buttonTable}></Column>
          </DataTable>

          <Dialogs
            header="Información del cliente"
            visible={clientDialog}
            hide={hideDialogClient}
            texto="3"
          />

          <Dialog
            visible={taskDialog}
            style={{ width: "400px" }}
            header="Tareas"
            modal
            className="p-fluid"
            footer={taskDialogFooter}
            onHide={hideDialog}
          >
            <div className="field">
              <label className="label-cliente" htmlFor="cliente">
                Cliente
              </label>
              <InputText
                id="cliente"
                onChange={(e) => onInputChange(e, "cliente")}
                required
                autoFocus
              />
              {submitted && !task.cliente && (
                <small className="p-invalid">El cliente es requerido.</small>
              )}
            </div>
            <div className="field">
              <label htmlFor="observaciones">Observaciones</label>
              <InputTextarea
                id="observaciones"
                onChange={(e) => onInputChange(e, "observaciones")}
                required
                rows={3}
                cols={20}
              />
            </div>


            <div className="field">
              <label className="mb-3">Tipo de tarea</label>
              <div className="formgrid grid">
                <div className="field-radiobutton col-6">
                  <RadioButton
                    inputId="llamada"
                    name="llamada"
                    value="llamada"
                    onChange={onCategoryChange}
                    checked={task.tarea === "llamada"}
                  />
                  <label htmlFor="llamada">Llamada</label>
                </div>
                <div className="field-radiobutton col-6">
                  <RadioButton
                    inputId="visita"
                    name="visita"
                    value="visita"
                    onChange={onCategoryChange}
                    checked={task.tarea === "visita"}
                  />
                  <label htmlFor="visita">Visita</label>
                </div>
              </div>
            </div>
          </Dialog>

          <Dialogs
            header="Borrar tarea"
            visible={deleteTaskDialog}
            footer={deleteTaskDialogFooter}
            hide={hideDeleteProductDialog}
            texto="1"
          />

          <Dialogs
            header="Borrar tareas"
            visible={deleteTasksDialog}
            footer={deleteTasksDialogFooter}
            hide={hideDeleteProductDialog}
            texto="2"
          />
        </div>
      </div>
    </div>
  );
};

export default TareasTable;
