import React from 'react';

const UserContext = React.createContext();



function userReducer(state, action) {
  switch (action.type) {
    case 'set': {
      const demoDataToSave = JSON.stringify({userName:action.userData.userName,rol:action.userData.rol})
      localStorage.setItem("demoUserData",demoDataToSave)
      localStorage.setItem("userToken",action.tocken)
      return {userName:action.userData.userName,rol:action.userData.rol}
    }
    case 'delete': {
      localStorage.removeItem("demoUserData")
      localStorage.removeItem("userToken")
      return {userName:undefined,rol:undefined}
    }
    default: {
      throw new Error(`Unhandled action type: ${action.type}`)
    }
  }
}

function UserProvider({children}) {
  const demoSavedData = localStorage.getItem("demoUserData") 
  const token = localStorage.getItem("userToken") 

  const {userName,rol} = !token ? {userName:undefined,rol:undefined} : JSON.parse(demoSavedData)
  const userData = {userName:userName,rol:rol}

  const [state, dispatch] = React.useReducer(userReducer, userData)

  const value = {state, dispatch}
  return <UserContext.Provider value={value}>{children}</UserContext.Provider>
}

function useUserData() {
  const context = React.useContext(UserContext)
  if (context === undefined) {
    throw new Error('useUserData must be used within a userProvider')
  }
  return context
}

export {UserProvider, useUserData}
