import { fakeBackend } from "./agent/fakeBackend";
import Queries from "./queries/queries"


async function apiAgent(url = '', data = {},type="GET") {
  // Opciones por defecto estan marcadas con un *
  const Body = type === "GET" ? {} : {body: JSON.stringify(data)}
  console.log(data);
  // const headers = data.token ? 
  const response = await fetch(url, {
    method: type, // *GET, POST, PUT, DELETE, etc.
    mode: 'cors', // no-cors, *cors, same-origin
    cache: 'no-cache', // *default, no-cache, reload, force-cache, only-if-cached
    credentials: 'same-origin', // include, *same-origin, omit

    headers: {
      'Content-Type': 'application/json'
    },
    redirect: 'follow', // manual, *follow, error
    referrerPolicy: 'no-referrer', // no-referrer, *no-referrer-when-downgrade, origin, origin-when-cross-origin, same-origin, strict-origin, strict-origin-when-cross-origin, unsafe-url
    ...Body
  });
  return response.json(); // parses JSON response into native JavaScript objects
}

const Productor = async (props) =>{
    const {query,data,type} = props
    const url = Queries[query]

    const result = await apiAgent(url,data,type)
    if(result.error) throw result.errorMsg
    return result
}

export default Productor

