import "primereact/resources/themes/lara-light-indigo/theme.css";
import "primereact/resources/primereact.min.css";
import "primeicons/primeicons.css";

import {useNavigate} from "react-router-dom";

import Header from "../components/Header/header.component";
import RolProvider from "../utils/rol.provider";

const Layout = ({ children }) => {
  const navigate = useNavigate();

  const admin = [
    {
      label: "Tareas",
      icon: "pi pi-fw pi-pencil",
      command: () => {navigate('/') },
    },
    {
      label: "Salir",
      icon: "pi pi-fw pi-power-off",
      command: () => {navigate('/logout') },
    },
    {
        label: "Gestión de tareas",
        icon: "pi pi-fw pi-user-plus",
        command: () => {navigate('/gestion') },
      },
      {
        label: "Gestión de usuarios",
        icon: "pi pi-fw pi-user",
        command: () => {navigate('/usuarios') },
      },
  ];

  const usuario = [
    {
      label: "Tareas",
      icon: "pi pi-fw pi-pencil",
      command: () => {navigate('/') },
    },
    {
      label: "Salir",
      icon: "pi pi-fw pi-power-off",
      command: () => {navigate('/logout') },
    },
  ];

  const superusuario = [
    {
      label: "Tareas",
      icon: "pi pi-fw pi-pencil",
      command: () => {navigate('/') },
    },
    {
      label: "Salir",
      icon: "pi pi-fw pi-power-off",
      command: () => {navigate('/logout') },
    },
    {
        label: "Gestión de tareas",
        icon: "pi pi-fw pi-user-plus",
        command: () => {navigate('/gestion') },
      },
      {
        label: "Gestión de usuarios",
        icon: "pi pi-fw pi-user",
        command: () => {navigate('/usuarios') },
      },
  ];

  return (
    <>
      <RolProvider rolToBeEqual="admin"> <Header rol={"admin"} menu={admin}/> </RolProvider>
      <RolProvider rolToBeEqual="usuario"> <Header rol={"usuario"}  menu={usuario}/> </RolProvider>
      <RolProvider rolToBeEqual="superusuario"> <Header rol={"superusuario"}  menu={superusuario}/> </RolProvider>
      <main>{children}</main>
    </>
  );
};

export default Layout;
