import React, { useState, useRef } from "react";
import { useFormik } from "formik";
import { InputText } from "primereact/inputtext";
import { Button } from "primereact/button";
import { Password } from "primereact/password";
import { classNames } from "primereact/utils";
import { Toast } from "primereact/toast";

import "./FormDemo.css";


export const Form = ({ action, error }) => {
  const [ , setShowMessage] = useState(false);
  const [ , setFormData] = useState({});

  const toast = useRef(null);


  const showError = (error) => {
    toast.current.show({
      severity: "error",
      summary: "Error Message",
      detail: error,
      life: 3000,
    });
  };

  if (error) {
    showError(error);
    window.location.reload(true);

  }

  const formik = useFormik({
    initialValues: {
      userName: "",
      userPassword: "",
    },
    validate: (data) => {
      let errors = {};

      if (!data.userName) {
        errors.userName = "El nombre es requerido.";
      }

      if (!data.userPassword) {
        errors.userPassword = "La contraseña es requerida.";
      }

      return errors;
    },
    onSubmit: (data) => {
      setFormData(data);
      action(data);
      setShowMessage(true);

      formik.resetForm();
    },
  });

  const isFormFieldValid = (name) =>
    !!(formik.touched[name] && formik.errors[name]);
  const getFormErrorMessage = (name) => {
    return (
      isFormFieldValid(name) && (
        <small className="p-error">{formik.errors[name]}</small>
      )
    );
  };

  return (
    <div className="form-demo">
      <Toast ref={toast} />
      <div className="flex justify-content-center">
        <div className="card">
          <h2 className="text-center">Acceso Agente Comercial</h2>
          <form onSubmit={formik.handleSubmit} className="p-fluid">
            <div className="field">
              <span className="p-float-label">
                <InputText
                  id="user"
                  name="userName"
                  value={formik.values.userName}
                  onChange={formik.handleChange}
                  autoFocus
                  className={classNames({
                    "p-invalid": isFormFieldValid("userName"),
                  })}
                />
                <label
                  htmlFor="userName"
                  className={classNames({
                    "p-error": isFormFieldValid("userName"),
                  })}
                >
                  Nombre*
                </label>
              </span>
              {getFormErrorMessage("userName")}
            </div>
            <div className="field">
              <span className="p-float-label">
                <Password
                  id="password"
                  name="userPassword"
                  value={formik.values.userPassword}
                  onChange={formik.handleChange}
                  className={classNames({
                    "p-invalid": isFormFieldValid("userPassword"),
                  })}
                />
                <label
                  htmlFor="password"
                  className={classNames({
                    "p-error": isFormFieldValid("userPassword"),
                  })}
                >
                  Contraseña*
                </label>
              </span>
              {getFormErrorMessage("userPassword")}
            </div>

            <Button
              type="submit"
              label="Acceder"
              className="mt-2 p-button-warning"
            />
          </form>
        </div>
      </div>
    </div>
  );
};

export default Form;
