import { Dialog } from "primereact/dialog";

const textos = {
   1: "¿Estás seguro que quieres eliminar la tarea?",
   2: "¿Estás seguro que quieres eliminar las tareas?",
   3: "Lorem ipsum dolor sit amet, consectetur adipiscing elit,"
}

const Dialogs = ({header, visible, footer, hide, texto}) => {
  return (
    <Dialog
      visible={visible}
      style={{ width: "450px" }}
      breakpoints={{ "960px": "75vw", "640px": "100vw" }}
      header={header}
      modal
      footer={footer}
      onHide={hide}
    >
      <div className="flex align-items-center justify-content-center">
        <span>{textos[texto]}</span>
      </div>
    </Dialog>
  );
};

export default Dialogs;
